    private void verifyBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_verifyBtnActionPerformed

    }//GEN-LAST:event_verifyBtnActionPerformed

    String key = "ahmed-saleh";

    public String getKey() {
        return key;
    }

    public void setKey(String key) {
        this.key = key;
    }
    
    private void verifyBtnMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_verifyBtnMouseClicked
        int ctr = 0;
        boolean warnMSG;
        if (keyInput.getText().equals(key)) {
            System.out.println("=========== A true credentials has been provided ===========");
            verifyBtn.hide();
            warnMSG = true;
        } else {
            for (int i = 0; i <= 0; i++) {
                ctr++;
                System.out.println("=========== A false credentials has been provided =========== " + ctr + " times");
            }
            warnMSG = false;
        }
        if (warnMSG != false) {
            errorMSG.hide();
            loadingMSG.setForeground(new java.awt.Color(16, 81, 3));
        } else {
            errorMSG.setForeground(Color.red);
            loadingMSG.setForeground(new java.awt.Color(245, 245, 245));
        }
        
    }//GEN-LAST:event_verifyBtnMouseClicked


